    private void  setBannerMoviePageAdapter(List<Banner>bannerList){
        bannerListViewPager=(ViewPager) findViewById(R.id.banner_view);

        bannerMoviePageAdapter = new BannerMoviePageAdapter(this,bannerList);

        bannerListViewPager.setAdapter(bannerMoviePageAdapter);

        //current page indicator

        indicatorTab.setupWithViewPager(bannerListViewPager);


        //set auto timer banner slider

        Timer sliderTimer = new Timer();
        sliderTimer.scheduleAtFixedRate(new AutoSlider(),4000,6000);
        indicatorTab.setupWithViewPager(bannerListViewPager,true);


    }
